import React from 'react';
import ReactPlayer from 'react-player/lazy';

interface Skill {
    name: string;
    icon: string | JSX.Element;
}

interface ProjectData {
    id: number;
    title: string;
    subtitle: string;
    image: string;
    description: string;
    skills: Skill[];
    highlights?: string[];
    review: string;
    link?: string;
    video?: string;
}

export default function Project() {
    const projects: ProjectData[] = [
        {
            id: 1,
            title: '약매니저',
            subtitle: 'React Native 앱 개발 및 유지보수 (2년)',
            image: 'https://bangmim.github.io/pmh/img/pmh.jpg',
            description:
                '약국 및 의료기관을 위한 종합 관리 앱입니다. 2년간 개발 및 유지보수를 담당했으며, 현재 48,000명의 사용자가 이용하고 있습니다.',
            skills: [
                { name: 'React Native', icon: '📱' },
                { name: 'TypeScript', icon: '🔷' },
                { name: 'iOS / Android', icon: '📲' },
                { name: 'Redux', icon: '🔄' },
                { name: 'REST API', icon: '🔌' },
                { name: 'Firebase', icon: '🔥' },
            ],
            highlights: [
                '커뮤니티 기능: 댓글/좋아요 시스템, 실시간 알림 구현',
                '마약류 점검부: 법적 요구사항을 충족하는 관리 시스템 개발',
                '약국 관리: 재고 관리 시스템 및 통계 대시보드 구현',
            ],
            review: '실제 서비스를 운영하며 사용자 피드백을 즉각 반영하는 경험을 쌓았습니다. 48,000명의 사용자를 대상으로 하는 만큼 성능 최적화와 안정성 확보에 중점을 두었으며, TypeScript 도입을 통해 코드 품질과 유지보수성을 개선했습니다. 앱 스토어 배포부터 버전 관리, 사용자 문의 대응까지 앱 운영의 전 과정을 경험하며 실무 역량을 향상시켰습니다.',
        },
        {
            id: 2,
            title: '약매니저 관리 웹',
            subtitle: 'React 기반 관리자 대시보드',
            image: 'https://bangmim.github.io/pmh/img/pmh.jpg',
            description:
                '약매니저 앱과 연동되는 관리자 대시보드입니다. 사용자 관리, 게시글 관리, 통계 확인 등 앱 운영에 필요한 기능들을 웹에서 관리할 수 있습니다.',
            skills: [
                { name: 'React', icon: '⚛️' },
                { name: 'TypeScript', icon: '🔷' },
                { name: 'REST API', icon: '🔌' },
                { name: 'Chart.js', icon: '📊' },
            ],
            highlights: [
                '사용자 관리 및 권한 설정 시스템 구현',
                '콘텐츠 관리 시스템 (게시글, 댓글 CRUD)',
                '실시간 통계 대시보드 개발 (Chart.js 활용)',
            ],
            review: 'Chart.js를 활용하여 데이터 시각화 기능을 구현하며, 사용자가 직관적으로 데이터를 파악할 수 있도록 했습니다. 앱과 웹을 함께 개발하면서 전체 서비스 아키텍처를 이해할 수 있었으며, 효율적인 API 설계와 상태 관리의 중요성을 경험했습니다.',
        },
    ];


    return (
        <div className="animate-slide-up">
            {/* 실무 프로젝트 */}
            <div className="mb-16">
                <h1 className="text-3xl font-bold text-gray-900 mb-8 pb-4 border-b-2 border-gray-900 flex items-center gap-2">
                    <span>💼</span>
                    실무 프로젝트
                </h1>
                <div className="space-y-16">
                    {projects.map((project) => (
                        <div key={project.id} className="section">
                            {/* 헤더 */}
                            <div className="mb-6">
                                <h2 className="text-2xl md:text-3xl font-bold text-gray-900 mb-2">{project.title}</h2>
                                <h3 className="text-xl text-gray-600 font-medium">{project.subtitle}</h3>
                            </div>

                            {/* 설명 */}
                            <div className="mb-6">
                                <p className="text-lg text-gray-700 leading-relaxed mb-4">{project.description}</p>

                                {/* 주요 기능 */}
                                {project.highlights && (
                                    <div className="mt-6">
                                        <h4 className="text-sm font-semibold text-gray-500 uppercase tracking-wider mb-3">
                                            주요 개발 기능
                                        </h4>
                                        <ul className="space-y-2">
                                            {project.highlights.map((highlight, index) => (
                                                <li key={index} className="flex items-start gap-2 text-gray-700">
                                                    <span className="text-gray-900 mt-1">•</span>
                                                    <span>{highlight}</span>
                                                </li>
                                            ))}
                                        </ul>
                                    </div>
                                )}
                            </div>

                            {/* 기술 스택 */}
                            <div className="mb-6">
                                <h4 className="text-sm font-semibold text-gray-500 uppercase tracking-wider mb-3">
                                    Tech Stack
                                </h4>
                                <div className="flex flex-wrap gap-2">
                                    {project.skills.map((skill, index) => (
                                        <div
                                            key={index}
                                            className="flex items-center gap-2 px-4 py-2 bg-gray-100 text-gray-800 font-medium hover:bg-gray-200 transition-colors duration-200"
                                        >
                                            <span className="text-lg">{skill.icon}</span>
                                            <span>{skill.name}</span>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            {/* 프로젝트 회고 */}
                            <div className="p-6 bg-blue-50 border-l-4 border-blue-600">
                                <h4 className="text-blue-900 font-semibold mb-2 flex items-center gap-2">
                                    <span>💭</span>
                                    프로젝트 회고
                                </h4>
                                <p className="text-gray-800 leading-relaxed">{project.review}</p>
                            </div>

                            {/* 비디오 */}
                            {project.video && (
                                <div className="mt-6">
                                    <h4 className="text-lg font-semibold text-gray-900 mb-3 text-center">구현 영상</h4>
                                    <div className="overflow-hidden">
                                        <ReactPlayer
                                            url={project.video}
                                            width="100%"
                                            height="400px"
                                            playing={false}
                                            muted={false}
                                            controls={true}
                                            loop={true}
                                        />
                                    </div>
                                </div>
                            )}

                            {/* 링크 */}
                            {project.link && (
                                <a
                                    href={project.link}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="btn-minimal inline-flex items-center gap-2 mt-6"
                                >
                                    <span>프로젝트 보기</span>
                                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                        <path
                                            strokeLinecap="round"
                                            strokeLinejoin="round"
                                            strokeWidth={2}
                                            d="M14 5l7 7m0 0l-7 7m7-7H3"
                                        />
                                    </svg>
                                </a>
                            )}
                        </div>
                    ))}
                </div>
            </div>

            {/* 학습 프로젝트 */}
            <div className="section pt-16">
                <h2 className="text-2xl font-bold text-gray-900 mb-6 flex items-center gap-2">
                    <span>📚</span>
                    학습 프로젝트
                </h2>
                <p className="text-gray-600 mb-6">프론트엔드 개발 학습 과정에서 진행한 프로젝트입니다.</p>
                <div className="space-y-4">
                    {learningProjects.map((project, index) => (
                        <div
                            key={index}
                            className="p-4 border-l-2 border-gray-300 hover:border-gray-900 transition-colors duration-200"
                        >
                            <h3 className="font-semibold text-gray-900 mb-1">{project.title}</h3>
                            <p className="text-sm text-gray-600 mb-2">{project.skills}</p>
                            <a
                                href={project.link}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="text-sm text-blue-600 hover:text-blue-800 font-medium"
                            >
                                보기 →
                            </a>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}
